class TrieNode:
    def __init__(self):
        self.children = {}
        self.is_end_of_word = False
        self.sum_pairs = []

def insert(root, word):
    node = root
    for char in word:
        if char not in node.children:
            node.children[char] = TrieNode()
        node = node.children[char]
    node.is_end_of_word = True

def map_sum_pairs(root, prefix):
    node = root
    for char in prefix:
        if char not in node.children:
            return
        node = node.children[char]
    dfs(node, prefix, node.sum_pairs)

def dfs(node, prefix, pairs):
    if node.is_end_of_word:
        pairs.append(prefix)
    for char, child in node.children.items():
        dfs(child, prefix + char, pairs)

def add(root, key, value):
    node = root
    for char in key:
        if char not in node.children:
            node.children[char] = TrieNode()
        node = node.children[char]
    node.is_end_of_word = True
    node.sum_pairs.append(value)
