class Trie:
    def __init__(self):
        self.children = {}
        self.is\_word = False

    def insert(self, word):
        node = self
        for char in word:
            if char not in node.children:
                node.children[char] = Trie()
            node = node.children[char]
        node.is\_word = True

    def search(self, word):
        node = self
        for char in word:
            if char not in node.children:
                return False
            node = node.children[char]
        return node.is\_word


def longest\_word\_length(dictionary):
    trie = Trie()
    for word in dictionary:
        trie.insert(word)
    longest\_word = ""
    for word in dictionary:
        if trie.search(word) and len(word) > len(longest\_word):
            longest\_word = word
    return len(longest\_word)


