class TrieNode:
    def __init__(self):
        self.children = {}
        self.is_word = False

class MagicDictionary:
    def __init__(self):
        self.root = TrieNode()

    def buildDict(self, words):
        for word in words:
            node = self.root
            for char in word:
                if char not in node.children:
                    node.children[char] = TrieNode()
                node = node.children[char]
            node.is_word = True

    def search(self, query):
        return self._search(self.root, query, False)

    def _search(self, node, query, mismatch):
        if not node:
            return False
        if query == '':
            return node.is_word
        if query[0] not in node.children:
            return mismatch
        result = False
        for child in node.children.values():
            result = result or self._search(child, query[1:], mismatch or child.is_word)
        return result
