class TrieNode:
    def __init__(self):
        self.children = {}
        self.is_end_of_word = False

class Trie:
    def __init__(self):
        self.root = TrieNode()

    def insert(self, word):
        node = self.root
        for char in word:
            if char not in node.children:
                node.children[char] = TrieNode()
            node = node.children[char]
        node.is_end_of_word = True

    def search(self, word):
        node = self.root
        for char in word:
            if char not in node.children:
                return False
            node = node.children[char]
        return node.is_end_of_word

    def starts_with(self, prefix):
        node = self.root
        for char in prefix:
            if char not in node.children:
                return False
            node = node.children[char]
        return True

def stream_of_characters(words):
    trie = Trie()
    for word in words:
        trie.insert(word)
    while True:
        char = input("Enter a character: ")
        if trie.starts_with(char):
            print("Valid prefix")
        else:
            print("Invalid prefix")
        if trie.search(char):
            print("Word found")
        else:
            print("Word not found")

stream_of_characters(["apple", "application", "banana", "bat"])
