class TrieNode:
    def __init__(self):
        self.children = {}
        self.is_word = False

class WordFilter:
    def __init__(self):
        self.root = TrieNode()

    def add_word(self, word):
        node = self.root
        for char in word:
            if char not in node.children:
                node.children[char] = TrieNode()
            node = node.children[char]
        node.is_word = True

    def checkExist(self, word):
        node = self.root
        for char in word:
            if char not in node.children:
                return False
            node = node.children[char]
        return node.is_word

# Test the word filter
word_filter = WordFilter()
word_filter.add_word("hello")
word_filter.add_word("world")
print(word_filter.checkExist("hello"))  # True
print(word_filter.checkExist("world"))  # True
print(word_filter.checkExist("helloworld"))  # False
print(word_filter.checkExist("goodbye"))  # False
