from collections import defaultdict

def detect_cycle(graph):
    visited = set()
    visiting = set()
    for node in graph:
        if node not in visited and dfs(graph, node, visited, visiting):
            return True
    return False

def dfs(graph, node, visited, visiting):
    visited.add(node)
    visiting.add(node)
    for neighbor in graph[node]:
        if neighbor not in visited and dfs(graph, neighbor, visited, visiting):
            return True
        elif neighbor in visiting:
            return True
    visiting.remove(node)
    return False

graph = defaultdict(list)
graph[1].append(2)
graph[2].append(3)
graph[3].append(1)

print(detect_cycle(graph))
