def local_minimum(arr):
    n = len(arr)
    low = 0
    high = n - 1

    while low <= high:
        mid = low + (high - low) // 2

        if (mid == 0 or arr[mid - 1] > arr[mid]) and (mid == n - 1 or arr[mid + 1] > arr[mid]):
            return arr[mid]
        elif mid > 0 and arr[mid - 1] < arr[mid]:
            high = mid - 1
        else:
            low = mid + 1

    return None
