from collections import deque

def minimum_vertices(graph, yaz):
    n = len(graph)
    visited = [False] * n
    queue = deque([yaz])
    visited[yaz] = True

    while queue:
        node = queue.popleft()
        for neighbor in graph[node]:
            if not visited[neighbor]:
                visited[neighbor] = True
                queue.append(neighbor)

    return sum(1 for v in visited if not v)
