class UnionFind:
    def __init__(self, n):
        self.parent = list(range(n))
        self.rank = [0] * n

    def find(self, x):
        if self.parent[x] != x:
            self.parent[x] = self.find(self.parent[x])
        return self.parent[x]

    def union(self, x, y):
        root_x = self.find(x)
        root_y = self.find(y)
        if root_x != root_y:
            if self.rank[root_x] < self.rank[root_y]:
                self.parent[root_x] = root_y
            elif self.rank[root_x] > self.rank[root_y]:
                self.parent[root_y] = root_x
            else:
                self.parent[root_y] = root_x
                self.rank[root_x] += 1

def valid_tree(edges):
    uf = UnionFind(len(edges))
    for i, (u, v) in enumerate(edges):
        if uf.find(i) == uf.find(v):
            print("Not a valid tree")
            return False
        uf.union(i, v)
    return True

edges = [[0, 1], [1, 2], [2, 3]]
print(valid_tree(edges))
