class UnionFind:
    def __init__(self):
        self.parent = {}

    def find(self, x):
        if x not in self.parent:
            self.parent[x] = x
        elif self.parent[x] != x:
            self.parent[x] = self.find(self.parent[x])
        return self.parent[x]

    def union(self, x, y):
        self.parent[self.find(x)] = self.find(y)

def accountsMerge(accounts):
    uf = UnionFind()
    email_to_name = {}
    email_to_id = {}
    id = 0

    for account in accounts:
        name = account[0]
        for email in account[1:]:
            email_to_name[email] = name
            if email not in email_to_id:
                email_to_id[email] = id
                id += 1
            uf.union(email_to_id[account[1]], email_to_id[email])

    merged_accounts = {}
    for email in email_to_name:
        root = uf.find(email_to_id[email])
        if root not in merged_accounts:
            merged_accounts[root] = []
        merged_accounts[root].append(email)

    return [[email_to_name[root]] + sorted(emails) for root, emails in merged_accounts.items()]
