class UnionFind:
    def __init__(self, n):
        self.parent = list(range(n))
        self.rank = [0]*n

    def find(self, x):
        if self.parent[x] != x:
            self.parent[x] = self.find(self.parent[x])
        return self.parent[x]

    def union(self, x, y):
        rootX = self.find(x)
        rootY = self.find(y)
        if rootX == rootY:
            return
        if self.rank[rootX] > self.rank[rootY]:
            self.parent[rootY] = rootX
        elif self.rank[rootX] < self.rank[rootY]:
            self.parent[rootX] = rootY
        else:
            self.parent[rootY] = rootX
            self.rank[rootX] += 1

def solution(land, height):
    n = len(land)
    uf = UnionFind(n*n)
    for i in range(n):
        for j in range(n):
            if i < n-1 and abs(land[i][j] - land[i+1][j]) <= height:
                uf.union(i*n+j, (i+1)*n+j)
            if j < n-1 and abs(land[i][j] - land[i][j+1]) <= height:
                uf.union(i*n+j, i*n+j+1)
    provinces = set()
    for i in range(n):
        for j in range(n):
            provinces.add(uf.find(i*n+j))
    return len(provinces)

land = [[1,2,2,3,5],[3,4,3,4,6],[5,6,7,8,9],[8,9,10,11,12],[15,16,17,18,19]]
height = 2
print(solution(land, height))
