class UnionFind:
    def __init__(self, n):
        self.parent = list(range(n))
        self.rank = [0] * n

    def find(self, x):
        if self.parent[x]!= x:
            self.parent[x] = self.find(self.parent[x])
        return self.parent[x]

    def union(self, x, y):
        root_x = self.find(x)
        root_y = self.find(y)
        if root_x!= root_y:
            if self.rank[root_x] > self.rank[root_y]:
                self.parent[root_y] = root_x
            elif self.rank[root_x] < self.rank[root_y]:
                self.parent[root_x] = root_y
            else:
                self.parent[root_y] = root_x
                self.rank[root_x] += 1

    def connected(self, x, y):
        return self.find(x) == self.find(y)

def smallest_string_with_swaps(s, indices):
    n = len(s)
    uf = UnionFind(n)
    for i, c in enumerate(s):
        if c == '1':
            for j in range(i-1, i+2):
                if 0 <= j < n and s[j] == '1':
                    uf.union(i, j)

    res = [[] for _ in range(n)]
    for i, c in enumerate(s):
        if c == '1':
            for j in range(n):
                if uf.connected(i, j) and s[j] == '0':
                    res[uf.find(i)].append(j)

    for i in range(n):
        res[i].sort()
        if res[i]:
            res[i].insert(0, i)

    min_len = float('inf')
    for i in range(n):
        curr_len = len(res[i])
        res[i] = [j for j in res[i] if j < i]
        for j in range(i-1, -1, -1):
            if uf.connected(i, j):
                curr_len += len(res[j])
                res[i].extend(res[j])
                break
        min_len = min(min_len, curr_len)

    return ''.join('1' if i in res[uf.find(i)] else '0' for i in range(n))
