class UnionFind:
    def __init__(self, n):
        self.parent = [i for i in range(n)]

    def find(self, x):
        if self.parent[x] != x:
            self.parent[x] = self.find(self.parent[x])
        return self.parent[x]

    def union(self, x, y):
        root_x = self.find(x)
        root_y = self.find(y)
        if root_x != root_y:
            self.parent[root_x] = root_y

n = 10
uf = UnionFind(n)
uf.union(0, 1)
uf.union(2, 3)
uf.union(4, 5)
uf.union(6, 7)
uf.union(8, 9)
uf.union(1, 3)
uf.union(5, 7)
uf.union(3, 6)

regions = {}
for i in range(n):
    root = uf.find(i)
    if root in regions:
        regions[root].append(i)
    else:
        regions[root] = [i]

