import networkx as nx
import networkx.algorithms.connectivity as nx_conn

def remove_edges_to_keep_traversable(graph, n):
    while True:
        num_edges_before = len(graph.edges())
        graph.remove_edges_from(nx_conn.edge_cut(graph, n))
        if len(graph.edges()) == num_edges_before:
            break
    return graph
