import heapq

def check_paths(graph, m, n):
    pq = [(0, 0, 0)]  # (distance, x, y)
    visited = set((0, 0))
    while pq:
        d, x, y = heapq.heappop(pq)
        if x == m - 1 and y == n - 1:
            return True
        for dx, dy in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
            nx, ny = x + dx, y + dy
            if 0 <= nx < m and 0 <= ny < n and (nx, ny) not in visited:
                if graph[nx][ny] == 0:
                    heapq.heappush(pq, (d + 1, nx, ny))
                    visited.add((nx, ny))
    return False
