class UnionFind:
    def __init__(self,n):
        self.parent=list(range(n))
        self.size=len(self.parent)
    def find(self,x):
        if self.parent[x]!=x:
            self.parent[x]=self.find(self.parent[x])
        return self.parent[x]
    def union(self,x,y):
        x_root=self.find(x)
        y_root=self.find(y)
        if x_root==y_root:return
        if self.size[x_root]<self.size[y_root]:
            self.parent[x_root]=y_root
        elif self.size[x_root]>self.size[y_root]:
            self.parent[y_root]=x_root
        else:
            self.parent[y_root]=x_root
            self.size[x_root]+=1

def minMalwareSpread(edges,n):
    uf=UnionFind(n)
    for u,v in edges:
        uf.union(u,v)
    return uf.size
