def build_tree(arr, n):
    tree = [float('inf')] * (4 * n)
    def seg_tree(node, start, end):
        if start == end:
            tree[node] = arr[start]
        else:
            mid = (start + end) // 2
            seg_tree(2 * node, start, mid)
            seg_tree(2 * node + 1, mid + 1, end)
            tree[node] = min(tree[2 * node], tree[2 * node + 1])
    seg_tree(1, 0, n - 1)
    return tree

def query_tree(tree, node, start, end, left, right):
    if left > end or right < start:
        return float('inf')
    if left <= start and right >= end:
        return tree[node]
    mid = (start + end) // 2
    p1 = query_tree(tree, 2 * node, start, mid, left, right)
    p2 = query_tree(tree, 2 * node + 1, mid + 1, end, left, right)
    return min(p1, p2)

def update_tree(tree, node, start, end, index, val):
    if start == end:
        tree[node] = val
    else:
        mid = (start + end) // 2
        if index <= mid:
            update_tree(tree, 2 * node, start, mid, index, val)
        else:
            update_tree(tree, 2 * node + 1, mid + 1, end, index, val)
        tree[node] = min(tree[2 * node], tree[2 * node + 1])

def range_min_query(arr, n, left, right):
    tree = build_tree(arr, n)
    return query_tree(tree, 1, 0, n - 1, left, right)

def update_query(arr, n, index, val):
    tree = build_tree(arr, n)
    update_tree(tree, 1, 0, n - 1, index, val)
    return arr

