class FenwickTree:
    def __init__(self, n):
        self.tree = [0] * (n + 1)

    def lowbit(self, x):
        return x & -x

    def update(self, x, val):
        while x < len(self.tree):
            self.tree[x] += val
            x += self.lowbit(x)

    def query(self, x):
        res = 0
        while x > 0:
            res += self.tree[x]
            x -= self.lowbit(x)
        return res

    def range_sum_query(self, left, right):
        return self.query(right) - self.query(left - 1)


if __name__ == '__main__':
    ft = FenwickTree(10)
    ft.update(1, 3)
    ft.update(2, 2)
    ft.update(6, 4)
    print(ft.range_sum_query(1, 2))  # 5
    print(ft.range_sum_query(2, 6))  # 11
    print(ft.range_sum_query(6, 10)) # 4
    print(ft.range_sum_query(1, 10)) # 20
