import requests

class SessionManager:
    def __init__(self, api_url='http://localhost:8000'):
        self.api_url = api_url
        self._token = None

    def get_token(self):
        response = requests.post(f'{self.api_url}/auth/login', json={'username': 'your_username', 'password': 'your_password'})
        if response.status_code == 200:
            self._token = response.json()['token']
            return self._token
        else:
            return None

    def create_session(self):
        if self._token:
            response = requests.post(f'{self.api_url}/sessions', headers={'Authorization': f'Bearer {self._token}'), json={'session_name': 'your_session_name'})
            if response.status_code == 201:
                return response.json()['session_id']
            else:
                return None
        else:
            return None

    def get_session(self, session_id):
        if self._token:
            response = requests.get(f'{self.api_url}/sessions/{session_id}', headers={'Authorization': f'Bearer {self._token}'})
            if response.status_code == 200:
                return response.json()
            else:
                return None
        else:
            return None

    def delete_session(self, session_id):
        if self._token:
            response = requests.delete(f'{self.api_url}/sessions/{session_id}', headers={'Authorization': f'Bearer {self._token}'})
            if response.status_code == 204:
                return True
            else:
                return False
        else:
            return False
