import concurrent.futures
import requests

def get_page(url):
    return requests.get(url).json()

def main():
    urls = [
        'https://api.github.com/users/user1',
        'https://api.github.com/users/user2',
        'https://api.github.com/users/user3',
        'https://api.github.com/users/user4',
        'https://api.github.com/users/user5'
    ]

    with concurrent.futures.ThreadPoolExecutor(max_workers=5) as executor:
        futures = [executor.submit(get_page, url) for url in urls]
        for future in concurrent.futures.as_completed(futures):
            try:
                page = future.result()
                print(page)
            except Exception as e:
                print(f'An error occurred: {e}')

if __name__ == '__main__':
    main()
