from flask import request, Response

class MyMiddleware:
    def __init__(self, app):
        self.app = app

    def __call__(self, view, *args, **kwargs):
        print("Middleware: Before view function call")
        response = view(*args, **kwargs)
        print("Middleware: After view function call")
        return response

app = Flask(__name__)

@app.before_request
def before_request():
    print("Before request")

@app.after_request
def after_request(response):
    print("After request")
    return response

@app.route('/')
def hello_world():
    return 'Hello, World!'

app.wsgi_app = MyMiddleware(app.wsgi_app)

if __name__ == '__main__':
    app.run()
