from flask import Flask, session
app = Flask(__name__)
app.secret_key = 'development_key'

@app.route('/set/<key>/<value>')
def set_session(key, value):
    session[key] = value
    return f'Set {key} to {value}'

@app.route('/get/<key>')
def get_session(key):
    if key in session:
        return f'{key}: {session[key]}'
    else:
        return f'{key} not found in session'

@app.route('/clear/<key>')
def clear_session(key):
    if key in session:
        session.pop(key)
    return f'Removed {key} from session'

@app.route('/clear')
def clear_session_all():
    session.clear()
    return 'Cleared all session keys'

if __name__ == '__main__':
    app.run(debug=True)
