from fastapi import FastAPI, HTTPException, Depends, Security
from fastapi.security import OAuth2PasswordBearer

app = FastAPI()

api_key_scheme = OAuth2PasswordBearer(tokenUrl="token")

@app.get("/protected-route")
async def protected\_route(api\_key: str = Security(api\_key\_scheme)):
if api\_key != "my-secret-api-key":
raise HTTPException(status\_code=400, detail="Invalid API key")
return {"message": "This is a protected route"}
