from fastapi import FastAPI

app = FastAPI()

@app.get("/")
async def read_root():
    return {"message": "Hello World"}

@app.get("/users/")
async def read_users():
    users = [
        {"id": 1, "name": "John"},
        {"id": 2, "name": "Jane"},
        {"id": 3, "name": "Bob"}
    ]
    return users

@app.get("/users/{user_id}")
async def read_user(user_id: int):
    user = {"id": user_id, "name": "John"}
    return user
