import math

def bellman_ford(graph, source):
    n = len(graph)
    dist = [math.inf] * n
    dist[source] = 0
    for i in range(n-1):
        for u in range(n):
            for v, w in graph[u]:
                if dist[u] + w < dist[v]:
                    dist[v] = dist[u] + w
    for u in range(n):
        for v, w in graph[u]:
            if dist[u] + w < dist[v]:
                return False, dist
    return True, dist
