import os

def create_symlink(source, target):
    try:
        os.symlink(source, target)
        print(f"Symlink created: {target} -> {source}")
    except OSError as e:
        print(f"Error creating symlink: {e}")

def check_symlink(path):
    if os.path.islink(path):
        try:
            target = os.readlink(path)
            print(f"Symlink found: {path} -> {target}")
        except OSError as e:
            print(f"Error reading symlink: {e}")
    else:
        print(f"{path} is not a symlink")
