def compare_files(file1, file2, block_size=1024):
    with open(file1, 'rb') as f1, open(file2, 'rb') as f2:
        while True:
            block1 = f1.read(block_size)
            block2 = f2.read(block_size)
            if not block1 or not block2:
                return block1 == block2
            if block1 != block2:
                return False
