import pickle

def object_to_pickle(obj, filename):
    with open(filename, 'wb') as file:
        pickle.dump(obj, file)

def pickle_to_object(filename):
    with open(filename, 'rb') as file:
        return pickle.load(file)

# Test
obj = {'name': 'John', 'age': 30}
object_to_pickle(obj, 'person.pkl')

loaded_obj = pickle_to_object('person.pkl')
print(loaded_obj)
