from dataclasses import dataclass, asdict, astuple
import json

@dataclass
class Address:
    street: str
    number: int

@dataclass
class User:
    name: str
    age: int
    address: Address

def encode(user):
    return json.dumps(asdict(user))

def decode(data):
    raw = json.loads(data)
    args = astuple(Address(**raw['address']))
    return User(**raw, address=Address(*args))

user = User('John', 30, Address('Main St', 123))
encoded = encode(user)
decoded = decode(encoded)
assert decoded == user
