import collections

class Graph:
    def __init__(self, vertices):
        self.V = vertices
        self.graph = collections.defaultdict(list)
        self.transposed_graph = collections.defaultdict(list)

    def add_edge(self, u, v):
        self.graph[u].append(v)
        self.transposed_graph[v].append(u)

    def dfs(self, vertex, visited, stack):
        visited[vertex] = True
        for i in self.graph[vertex]:
            if not visited[i]:
                self.dfs(i, visited, stack)
        stack.append(vertex)

    def fill_order(self, vertex, visited, order):
        visited[vertex] = True
        order.append(vertex)
        for i in self.transposed_graph[vertex]:
            if not visited[i]:
                self.fill_order(i, visited, order)

    def get_transpose(self):
        return self.transposed_graph

    def kosaraju(self):
        visited = [False] * self.V
        stack = []
        for i in range(self.V):
            if not visited[i]:
                self.dfs(i, visited, stack)

        visited = [False] * self.V
        transpose = self.get_transpose()
        result = []
        while stack:
            i = stack.pop()
            if not visited[i]:
                temp = []
                self.fill_order(i, visited, temp)
                result.append(temp)

        return result

# Example Usage
g = Graph(5)
g.add_edge(1, 0)
g.add_edge(0, 2)
g.add_edge(2, 1)
g.add_edge(0, 3)
g.add_edge(3, 4)

print("Strongly Connected Components:")
print(g.kosaraju())
