def merge\_configs(*configs):
config\_sets = [dict(config) for config in configs]
merged\_config = config\_sets[0]
for config\_set in config\_sets[1:]:
for key, value in config\_set.items():
if key in merged\_config and isinstance(merged\_config[key], dict) and isinstance(value, dict):
merged\_config[key] = merge\_configs(merged\_config[key], value)
elif key in merged\_config and merged\_config[key] == value:
continue
else:
merged\_config[key] = value
return merged\_config
