import logging

class LogHandler(logging.Handler):
    def __init__(self, target_level):
        self.target_level = target_level
        super().__init__()

    def emit(self, record):
        if record.levelno >= self.target_level:
            self.acquire()
            try:
                self.target.emit(record)
            finally:
                self.release()
