import sys
import traceback

def exception_logger(func):
    def wrapper(*args, **kwargs):
        try:
            return func(*args, **kwargs)
        except Exception as e:
            error_message = f"Error in {func.__name__}: {str(e)}\n"
            error_details = "".join(traceback.format_exception(etype=type(e), value=e, tb=e.__traceback__))
            print(error_message + error_details, file=sys.stderr)
            return None
    return wrapper
