import statistics

class MetricsCollector:
    def __init__(self):
        self.metrics = {
            'accuracy': [],
            'precision': [],
            'recall': [],
            'f1_score': []
        }

    def add_accuracy(self, accuracy):
        self.metrics['accuracy'].append(accuracy)

    def add_precision(self, precision):
        self.metrics['precision'].append(precision)

    def add_recall(self, recall):
        self.metrics['recall'].append(recall)

    def add_f1_score(self, f1_score):
        self.metrics['f1_score'].append(f1_score)

    def get_accuracy_mean(self):
        return statistics.mean(self.metrics['accuracy'])

    def get_precision_mean(self):
        return statistics.mean(self.metrics['precision'])

    def get_recall_mean(self):
        return statistics.mean(self.metrics['recall'])

    def get_f1_score_mean(self):
        return statistics.mean(self.metrics['f1_score'])

    def get_accuracy_std(self):
        return statistics.stdev(self.metrics['accuracy'])

    def get_precision_std(self):
        return statistics.stdev(self.metrics['precision'])

    def get_recall_std(self):
        return statistics.stdev(self.metrics['recall'])

    def get_f1_score_std(self):
        return statistics.stdev(self.metrics['f1_score'])

    def get_accuracy_median(self):
        return statistics.median(self.metrics['accuracy'])

    def get_precision_median(self):
        return statistics.median(self.metrics['precision'])

    def get_recall_median(self):
        return statistics.median(self.metrics['recall'])

    def get_f1_score_median(self):
        return statistics.median(self.metrics['f1_score'])
