import datetime

class AlertTriggerConditionChecker:
    def __init__(self, condition):
        self.condition = condition

    def check(self, data):
        if self.condition == "date_range":
            start_date, end_date = data
            current_date = datetime.datetime.now()
            return start_date <= current_date <= end_date
        elif self.condition == "num_less_than":
            threshold = data
            return any(x < threshold for x in [1, 2, 3])
        elif self.condition == "str_match":
            pattern = data
            return any(pattern in str(x) for x in ["abc", "def", "ghi"])
        else:
            raise ValueError("Invalid condition")

class AlertTriggerCondition:
    def __init__(self, id, condition, data):
        self.id = id
        self.condition = condition
        self.condition_checker = AlertTriggerConditionChecker(condition)
        self.data = data

    def is_triggered(self, data):
        return self.condition_checker.check(self.data)

# example usage
condition = AlertTriggerCondition("date_range", "date_range", (datetime.datetime(2022, 1, 1), datetime.datetime(2022, 1, 10)))
print(condition.is_triggered(datetime.datetime.now()))
