import datetime
import inspect

class AuditLogger:
    def __init__(self, obj):
        self.obj = obj

    def __getattribute__(self, name):
        attr = super().__getattribute__(name)
        if name in ('wrap', 'original') and not inspect.isfunction(attr):
            return attr
        if not inspect.isfunction(attr):
            return attr
        wrapped = self.wrap(attr)
        return wrapped

    def wrap(self, func):
        def wrapper(*args, **kwargs):
            result = func(*args, **kwargs)
            now = datetime.datetime.now().strftime('%Y-%m-%d %H:%M:%S')
            argument_str = ', '.join(repr(arg) for arg in args)
            keyword_str = ', '.join(f'{key}={repr(value)}' for key, value in kwargs.items())
            log_msg = f'{now}: {func.__name__}({argument_str}, {keyword_str}) -> {repr(result)}'
            print(log_msg)
            return result
        return wrapper

    def original(self, func):
        return func
