def luhn_check(card_number):
    digits = [int(d) for d in str(card_number)]
    sum = 0
    for i, digit in enumerate(digits):
        if i % 2 == 0:
            sum += digit
        else:
            digit *= 2
            if digit > 9:
                digit -= 9
            sum += digit
    return sum % 10 == 0
