def is_valid_ipv4(ip):
    parts = ip.split('.')
    if len(parts) != 4:
        return False
    for part in parts:
        if not part.isdigit() or int(part) < 0 or int(part) > 255:
            return False
    return True

def is_valid_ipv6(ip):
    parts = ip.split(':')
    if len(parts) != 8:
        return False
    for part in parts:
        if len(part) < 1 or len(part) > 4 or not all(c in '0123456789abcdefABCDEF' for c in part):
            return False
    return True

def is_valid_ip(ip):
    if is_valid_ipv4(ip):
        return 'IPv4'
    elif is_valid_ipv6(ip):
        return 'IPv6'
    else:
        return 'Neither'
