import numpy as np
from collections import defaultdict

def johnson(graph):
    n = len(graph)
    dist = [[float('inf')] * n for _ in range(n)]
    for i in range(n):
        dist[i][i] = 0
    for u, v, w in graph:
        dist[u-1][v-1] = w
    for k in range(n):
        for i in range(n):
            for j in range(n):
                dist[i][j] = min(dist[i][j], dist[i][k] + dist[k][j])
    for i in range(n):
        if dist[i][i] < 0:
            return None
    return dist

graph = [(1, 2, 3), (2, 3, 1), (3, 1, -4)]
print(johnson(graph))
