def find(parent, i):
    if parent[i] == i:
        return i
    return find(parent, parent[i])

def union(parent, rank, x, y):
    x_root = find(parent, x)
    y_root = find(parent, y)

    if rank[x_root] < rank[y_root]:
        parent[x_root] = y_root
    elif rank[x_root] > rank[y_root]:
        parent[y_root] = x_root
    else:
        parent[y_root] = x_root
        rank[x_root] += 1

def boruvkaMST(graph, V):
    parent = []
    rank = []
    cheapest = []

    for node in range(V):
        parent.append(node)
        rank.append(0)
        cheapest.append(-1)

    num_trees = V
    mst_weight = 0

    while num_trees > 1:
        for i in range(len(graph)):
            u, v, w = graph[i]
            set1 = find(parent, u)
            set2 = find(parent, v)

            if set1 != set2:
                if cheapest[set1] == -1 or cheapest[set1][2] > w:
                    cheapest[set1] = [u, v, w]
                if cheapest[set2] == -1 or cheapest[set2][2] > w:
                    cheapest[set2] = [u, v, w]

        for node in range(V):
            if cheapest[node] != -1:
                u, v, w = cheapest[node]
                set1 = find(parent, u)
                set2 = find(parent, v)

                if set1 != set2:
                    mst_weight += w
                    union(parent, rank, set1, set2)
                    num_trees -= 1

        cheapest = [-1] * V

    return mst_weight
