def is_iban_valid(iban):
    iban = iban.upper()
    if not iban.isalnum() or len(iban) != 24:
        return False
    else:
        total = 0
        for i in range(2, len(iban), 2):
            total += int(iban[i - 2])
        for i in range(0, len(iban), 2):
            digit = int(iban[i]) * 2
            total += (digit // 10) + (digit % 10)
        return total % 97 == 0

# Test cases
print(is_iban_valid('TR33000610051978645723'))  # True
print(is_iban_valid('TR93000610051978901234'))  # False
