import time

def retry(exception, max_attempts=5, initial_delay=0.1, factor=2):
    def decorator(func):
        def wrapper(*args, **kwargs):
            attempts = 0
            success = False
            while attempts < max_attempts and not success:
                try:
                    return func(*args, **kwargs)
                except exception:
                    attempts += 1
                    time.sleep(initial_delay * (factor ** attempts))
            raise exception('Max attempts exceeded')
        return wrapper
    return decorator
