def cache(ttl):
    def decorator(func):
        cache_dict = {}
        def wrapper(*args, **kwargs):
            key = (args, tuple(kwargs.items()))
            if key in cache_dict and time.time() - cache_dict[key][1] < ttl:
                return cache_dict[key][0]
            result = func(*args, **kwargs)
            cache_dict[key] = (result, time.time())
            return result
        return wrapper
    return decorator
