def deprecation_warning(message, version="0.0.0"):
    def decorator(func):
        def wrapper(*args, **kwargs):
            import warnings
            warnings.simplefilter('always', DeprecationWarning)
            warnings.warn(f"Deprecation warning: {message}. This function will be removed in {version}.", category=DeprecationWarning, stacklevel=2)
            return func(*args, **kwargs)
        return wrapper
    return decorator
