def debounce(func, delay=0.5):
    def wrapper(*args, **kwargs):
        import time
        def inner():
            return func(*args, **kwargs)
        wrapper.timer = time.time()
        if not hasattr(wrapper, 'timer') or time.time() - wrapper.timer >= delay:
            wrapper.timer = time.time()
            return inner()
    return wrapper
