import time

def throttle(wait_time):
    def decorator(func):
        last_invocation = 0

        def wrapper(*args, **kwargs):
            nonlocal last_invocation
            elapsed_time = time.time() - last_invocation
            if elapsed_time < wait_time:
                time.sleep(wait_time - elapsed_time)
            last_invocation = time.time()
            return func(*args, **kwargs)

        return wrapper

    return decorator

@throttle(5)
def test_func():
    print("Function called")

test_func()
