def lazy_property(func):
    def wrapper(self):
        if not hasattr(self, '_wrapped'):
            setattr(self, '_wrapped', func(self))
    return property(wrapper)

class TestClass:
    @lazy_property
    def x(self):
        print("Calculating x")
        return 1 + 2

t = TestClass()
print(t.x)  # prints: Calculating x, then 3
print(t.x)  # just prints 3
