class MethodChain:
    def __init__(self, value):
        self.value = value

    def add(self, num):
        self.value += num
        return self

    def subtract(self, num):
        self.value -= num
        return self

    def multiply(self, num):
        self.value *= num
        return self

    def divide(self, num):
        self.value /= num
        return self

# Example usage
result = MethodChain(10).add(5).subtract(3).multiply(2).divide(4).value
print(result)
