class FluentString:
    def __init__(self, value):
        self.value = value

    def append(self, suffix):
        self.value += suffix
        return self

    def upper(self):
        self.value = self.value.upper()
        return self

    def lower(self):
        self.value = self.value.lower()
        return self

    def __str__(self):
        return self.value


if __name__ == "__main__":
    print(
        FluentString("hello")
        .append(" world")
        .upper()
        .append("!")
        .lower()
    )
