def check_number(num):
    if num > 0:
        if num % 2 == 0:
            if num % 4 == 0:
                return "Number is positive, even and divisible by 4"
            else:
                return "Number is positive and even but not divisible by 4"
        else:
            return "Number is positive but not even"
    else:
        return "Number is not positive"
